mapper.select:
select *
from (
select rownum rn, a.*
from (
select *
from post
order by post_code desc
) a
)
where rn between #{start} and #{end}

select emp_info_name
from emp_info
where emp_info_emp_no = #{emp_no}

mapper.update:
update post
set post_cvp = post_cvp + 1
where post_code = #{post_code}

update post
 set post_title = #{post_title}, post_comt=#{post_comt}, post_sec=#{post_sec}
 where post_code = #{post_code}
