mapper.insert:
insert into anva_hstr
	 ( anva_hstr_ordr
	 , anva_hstr_emp_no
	 , anva_hstr_type
	 , anva_hstr_rdat
	 , anva_hstr_sdat
	 , anva_hstr_edat
	 , anva_hstr_cont
	 , anva_hstr_conf
	 , anva_hstr_sign_code
	)
values
	 ( anhs_seq.nextval
	 , #{empNo}
	 , #{vacationType}
	 , #{regitDate}
	 , #{startDate}
	 , #{endDate}
	 , #{content}
	 , #{confirm}
	 , #{sign_code}
	 )

mapper.select:
select emp_info_dept_code
  from emp_info
 where emp_info_emp_no = #{emp_no}

		select emp_info_emp_no
          from emp_info
         where emp_info_dept_code = #{dept_code}
           and emp_info_job_code = (select min(emp_info_job_code)
                                      from emp_info
                                     where emp_info_dept_code = #{dept_code}
                                   )
